// Extract data from webhook
const message = $input.item.json.body.message;
const history = $input.item.json.body.history || [];
const context = $input.item.json.body.context || {};
const sessionId = $input.item.json.body.sessionId;

// Format conversation history for OpenAI
const messages = history.map(msg => ({
  role: msg.role === 'user' ? 'user' : 'assistant',
  content: msg.content
}));

// Add current message
messages.push({
  role: 'user',
  content: message
});

return {
  json: {
    messages,
    context,
    sessionId,
    currentMessage: message
  }
};

// Extract AI response
const aiMessage = $input.item.json.choices[0].message.content;

// Format response for PersonaFlow
return {
  json: {
    text: aiMessage,
    response: aiMessage,
    output: aiMessage,
    message: aiMessage,
    timestamp: new Date().toISOString(),
    model: 'gpt-3.5-turbo',
    sessionId: $input.item.json.sessionId
  }
};
