// Extract and enrich incoming data
const body = $input.item.json.body;
const message = body.message || '';
const history = body.history || [];
const context = body.context || {};
const userId = body.userId || context.userId || 'default-user';
const sessionId = body.sessionId || context.sessionId || 'session-' + Date.now();
const source = body.source || context.page || 'chat';

// Analyze message intent
const intent = detectIntent(message);

function detectIntent(msg) {
  const lower = msg.toLowerCase();
  if (lower.includes('journal') || lower.includes('wrote') || lower.includes('feeling')) return 'journal_query';
  if (lower.includes('habit') || lower.includes('streak') || lower.includes('progress')) return 'habit_query';
  if (lower.includes('therapy') || lower.includes('anxious') || lower.includes('stressed')) return 'therapy_query';
  if (lower.includes('insight') || lower.includes('pattern') || lower.includes('trend')) return 'analytics_query';
  return 'general_chat';
}

return {
  json: {
    message,
    history,
    context,
    userId,
    sessionId,
    source,
    intent,
    timestamp: new Date().toISOString(),
    mode: 'advanced'
  }
};

// Aggregate all context sources
const extractedData = $node['Extract & Enrich Data'].json;
const journalEntries = $node['Fetch Journal Entries'].json || [];
const habits = $node['Fetch Habits'].json || [];
const therapySessions = $node['Fetch Therapy Sessions'].json || [];
const similarEntries = $node['Vector Similarity Search'].json || [];

// Build comprehensive context
const context = {
  user: {
    id: extractedData.userId,
    sessionId: extractedData.sessionId,
    intent: extractedData.intent,
    source: extractedData.source
  },
  journal: {
    recent: journalEntries.slice(0, 5).map(e => ({
      date: new Date(e.created_at).toLocaleDateString(),
      mood: e.mood,
      preview: e.content.substring(0, 150),
      tags: e.tags
    })),
    similar: similarEntries.map(e => ({
      similarity: (1 - e.similarity).toFixed(2),
      mood: e.mood,
      preview: e.content.substring(0, 100),
      date: new Date(e.created_at).toLocaleDateString()
    }))
  },
  habits: habits.map(h => ({
    name: h.name,
    category: h.category,
    streak: h.streak,
    lastCompleted: new Date(h.last_completed).toLocaleDateString(),
    notes: h.notes
  })),
  therapy: therapySessions.map(t => ({
    date: new Date(t.created_at).toLocaleDateString(),
    mood: t.mood,
    type: t.session_type,
    summary: t.summary,
    insights: t.insights
  }))
};

// Calculate user insights
const insights = {
  totalJournalEntries: journalEntries.length,
  activeHabits: habits.filter(h => h.streak > 0).length,
  longestStreak: Math.max(...habits.map(h => h.streak), 0),
  recentMoods: journalEntries.slice(0, 5).map(e => e.mood),
  therapySessionCount: therapySessions.length
};

return {
  json: {
    message: extractedData.message,
    history: extractedData.history,
    context: context,
    insights: insights,
    timestamp: extractedData.timestamp
  }
};

const context = $input.item.json.context;
const journal = context.journal;

// Analyze mood patterns
const moodCounts = {};
journal.recent.forEach(entry => {
  moodCounts[entry.mood] = (moodCounts[entry.mood] || 0) + 1;
});

const dominantMood = Object.keys(moodCounts).reduce((a, b) => 
  moodCounts[a] > moodCounts[b] ? a : b
);

const analysis = {
  dominantMood,
  moodDistribution: moodCounts,
  recentTrend: journal.recent.slice(0, 3).map(e => e.mood).join(' â†’ '),
  totalEntries: journal.recent.length,
  commonThemes: extractThemes(journal.recent)
};

function extractThemes(entries) {
  const themes = [];
  entries.forEach(e => {
    if (e.tags && e.tags.length > 0) {
      themes.push(...e.tags);
    }
  });
  return [...new Set(themes)].slice(0, 5);
}

return { json: { analysis } };

const context = $input.item.json.context;
const habits = context.habits;

const analysis = {
  totalHabits: habits.length,
  activeHabits: habits.filter(h => h.streak > 0).length,
  longestStreak: Math.max(...habits.map(h => h.streak), 0),
  topHabits: habits.slice(0, 3).map(h => ({
    name: h.name,
    streak: h.streak,
    category: h.category
  })),
  categories: [...new Set(habits.map(h => h.category))],
  needsAttention: habits.filter(h => h.streak === 0).map(h => h.name)
};

return { json: { analysis } };

const context = $input.item.json.context;
const therapy = context.therapy;

const analysis = {
  totalSessions: therapy.length,
  recentMoods: therapy.map(t => t.mood),
  keyInsights: therapy.flatMap(t => t.insights || []).slice(0, 5),
  sessionTypes: [...new Set(therapy.map(t => t.type))],
  recentSummaries: therapy.slice(0, 2).map(t => ({
    date: t.date,
    summary: t.summary
  }))
};

return { json: { analysis } };

// Extract AI response
const aiOutput = $input.item.json.output || $input.item.json.text || "I'm here to support your personal growth journey.";
const contextData = $node['Aggregate RAG Context'].json;
const extractedData = $node['Extract & Enrich Data'].json;

// Clean and format response
const cleanResponse = aiOutput.trim();

// Prepare response with metadata
const response = {
  text: cleanResponse,
  response: cleanResponse,
  output: cleanResponse,
  message: cleanResponse,
  timestamp: new Date().toISOString(),
  model: 'llama3.1',
  mode: 'advanced',
  sessionId: extractedData.sessionId,
  userId: extractedData.userId,
  source: extractedData.source,
  intent: extractedData.intent,
  contextUsed: {
    journalEntries: contextData.context.journal.recent.length,
    similarEntries: contextData.context.journal.similar.length,
    habits: contextData.context.habits.length,
    therapySessions: contextData.context.therapy.length
  },
  insights: contextData.insights
};

return { json: response };

// Generate embedding for the AI response for future similarity search
const response = $node['Format AI Response'].json;
const message = response.text;

// This will be used to create embeddings for the response
return {
  json: {
    content: message,
    userId: response.userId,
    sessionId: response.sessionId,
    timestamp: response.timestamp
  }
};
