// Extract data from webhook
const body = $input.item.json.body;
const message = body.message || '';
const history = body.history || [];
const context = body.context || {};
const source = body.source || context.page || 'chat';
const sessionId = context.sessionId || 'demo-' + Date.now();

// Format conversation history for AI Agent
const chatHistory = history.map(msg => ({
  role: msg.role === 'user' ? 'user' : 'assistant',
  content: msg.content
}));

// Determine system prompt based on source
let systemPrompt = '';

if (source === 'therapy') {
  systemPrompt = `You are a compassionate AI therapist for PersonaFlow.

Your role:
- Provide empathetic, therapeutic responses
- Use active listening and reflection techniques
- Help users explore their feelings safely
- Ask thoughtful follow-up questions
- Validate emotions without judgment
- Keep responses warm but professional (2-4 sentences)

Tone: Gentle, understanding, therapeutic
Style: Socratic questioning, reflective listening

Context: This is a guided therapy session. The user is seeking emotional support and clarity.`;
} else {
  systemPrompt = `You are a compassionate AI companion for PersonaFlow, a personal growth and mental wellness app.

Your role:
- Provide warm, supportive responses
- Help users reflect on their thoughts and feelings
- Offer gentle guidance for personal growth
- Keep responses concise and meaningful (2-3 sentences)
- Be encouraging and non-judgmental
- Use a conversational, friendly tone

Context: This is a demo version showcasing the app's capabilities. Help users explore mindfulness, personal growth, and emotional wellness.`;
}

return {
  json: {
    message,
    chatHistory,
    source,
    context,
    sessionId,
    systemPrompt,
    mode: 'demo'
  }
};

// Extract AI response
const aiOutput = $input.item.json.output || $input.item.json.text || "I'm here to support you on your personal growth journey. How can I help you today?";

// Clean up the response
const cleanResponse = aiOutput.trim();

// Get session info
const sessionId = $node['Extract & Format Data'].json.sessionId;
const source = $node['Extract & Format Data'].json.source;

// Format response for PersonaFlow
return {
  json: {
    text: cleanResponse,
    response: cleanResponse,
    output: cleanResponse,
    message: cleanResponse,
    timestamp: new Date().toISOString(),
    model: 'samantha:mistral',
    mode: 'demo',
    source: source,
    sessionId: sessionId
  }
};
